import React, { useState, useEffect } from 'react';
import { Box, Container, Typography } from '@mui/material';
import { styled } from '@mui/material/styles';
import { keyframes } from '@mui/system';
import Values from './sections/Values';
import HotPosts from './sections/HotPosts';
import BestOutfit from './sections/BestOutfit';
import TodaysPoll from './sections/TodaysPoll';
import BrandDiscounts from './sections/BrandDiscounts';

// 팔레트 회전 애니메이션
const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const LeftPanel = styled(Box)(({ theme, bgcolor }) => ({
  width: '35%',
  height: '100vh',
  position: 'fixed',
  left: 0,
  top: 0,
  backgroundColor: bgcolor,
  transition: 'background-color 0.8s ease-in-out',
  display: 'flex',
  flexDirection: 'column',
  padding: '50px',
  color: '#fff',
}));

const RightPanel = styled(Box)(({ theme }) => ({
  width: '65%',
  minHeight: '100vh',
  marginLeft: '35%',
  backgroundColor: '#fff',
  position: 'relative',
  padding: '50px 60px',
  transition: 'opacity 0.5s ease-in-out',
}));

const PaletteContainer = styled(Box)(({ theme }) => ({
  width: '80px',
  height: '80px',
  position: 'relative',
  marginBottom: '40px',
}));

const PaletteSegment = styled(Box)(({ color, isActive }) => ({
  width: '80px',
  height: '80px',
  borderRadius: '50%',
  position: 'absolute',
  backgroundColor: color,
  opacity: isActive ? 1 : 0.7,
  transition: 'opacity 0.3s ease-in-out',
  cursor: 'pointer',
  '&:hover': {
    opacity: 1,
  },
}));

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const ContentContainer = styled(Box)(({ theme }) => ({
  animation: `${fadeIn} 0.5s ease-out forwards`,
}));

const sections = [
  {
    id: 'values',
    color: '#87CEEB',
    title: '우리의 가치관',
    component: Values,
  },
  {
    id: 'hot',
    color: '#FF6347',
    title: 'Hot 게시물',
    component: HotPosts,
  },
  {
    id: 'best-outfit',
    color: '#FFDA63',
    title: '오늘의 Best 코디',
    component: BestOutfit,
  },
  {
    id: 'poll',
    color: '#B0B0B0',
    title: '오늘의 투표',
    component: TodaysPoll,
  },
  {
    id: 'event',
    color: '#F5F5F5',
    title: '할인 정보 & Event',
    component: BrandDiscounts,
  },
];

const MainLayout = () => {
  const [currentSection, setCurrentSection] = useState(0);
  const [isAutoRotating, setIsAutoRotating] = useState(true);
  const [contentKey, setContentKey] = useState(0); // 컨텐츠 전환 애니메이션을 위한 키

  useEffect(() => {
    let interval;
    if (isAutoRotating) {
      interval = setInterval(() => {
        setCurrentSection((prev) => (prev + 1) % sections.length);
        setContentKey((prev) => prev + 1);
      }, 5000);
    }
    return () => clearInterval(interval);
  }, [isAutoRotating]);

  const handlePaletteClick = (index) => {
    setCurrentSection(index);
    setContentKey((prev) => prev + 1);
    setIsAutoRotating(false);
    // 10초 후 자동 회전 재개
    setTimeout(() => setIsAutoRotating(true), 10000);
  };

  const CurrentComponent = sections[currentSection].component;

  return (
    <Box sx={{ display: 'flex', height: '100vh', overflow: 'hidden' }}>
      <LeftPanel bgcolor={sections[currentSection].color}>
        <PaletteContainer>
          {sections.map((section, index) => (
            <PaletteSegment
              key={section.id}
              color={section.color}
              isActive={currentSection === index}
              onClick={() => handlePaletteClick(index)}
              sx={{
                transform: `rotate(${(360 / sections.length) * index}deg)`,
              }}
            />
          ))}
        </PaletteContainer>
        
        <Typography
          variant="h2"
          sx={{
            fontSize: '3.2rem',
            fontWeight: 700,
            marginBottom: '20px',
            textShadow: '0 2px 4px rgba(0,0,0,0.2)',
          }}
        >
          {sections[currentSection].title}
        </Typography>
      </LeftPanel>

      <RightPanel>
        <ContentContainer key={contentKey}>
          <CurrentComponent />
        </ContentContainer>
      </RightPanel>
    </Box>
  );
};

export default MainLayout;
