import React from 'react';
import { Box, Typography, Grid, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';
import ColorLensIcon from '@mui/icons-material/ColorLens';
import PaletteIcon from '@mui/icons-material/Palette';
import GroupsIcon from '@mui/icons-material/Groups';

const ValueItem = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  textAlign: 'center',
  color: theme.palette.text.secondary,
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  transition: 'transform 0.3s ease-in-out',
  '&:hover': {
    transform: 'translateY(-5px)',
  },
}));

const ValueIcon = styled(Box)(({ theme }) => ({
  fontSize: '3rem',
  marginBottom: theme.spacing(2),
  color: theme.palette.primary.main,
}));

const Values = () => {
  const values = [
    {
      icon: <ColorLensIcon sx={{ fontSize: 'inherit' }} />,
      title: '색상 인식의 다양성',
      description: '모든 사람이 색을 다르게 인식할 수 있음을 이해하고 존중합니다.',
    },
    {
      icon: <PaletteIcon sx={{ fontSize: 'inherit' }} />,
      title: '패션을 통한 자기표현',
      description: '색각이상이 패션을 통한 자기표현의 제약이 되지 않도록 돕습니다.',
    },
    {
      icon: <GroupsIcon sx={{ fontSize: 'inherit' }} />,
      title: '경험 공유의 가치',
      description: '색각이상을 가진 사용자들의 경험을 공유하고 서로 배웁니다.',
    },
  ];

  return (
    <Box sx={{ py: 4 }}>
      <Typography variant="h4" component="h2" gutterBottom align="center" sx={{ mb: 6 }}>
        우리의 가치
      </Typography>
      <Grid container spacing={4}>
        {values.map((value, index) => (
          <Grid item xs={12} md={4} key={index}>
            <ValueItem elevation={3}>
              <ValueIcon>{value.icon}</ValueIcon>
              <Typography variant="h6" component="h3" gutterBottom>
                {value.title}
              </Typography>
              <Typography variant="body1" color="text.secondary">
                {value.description}
              </Typography>
            </ValueItem>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Values;
