import React, { useState, useEffect } from 'react';
import { Box, Container, Typography, Grid, Card, CardContent, Divider } from '@mui/material';
import { styled } from '@mui/material/styles';
import { keyframes } from '@mui/system';
import Values from './sections/Values';
import HotPosts from './sections/HotPosts';
import BestOutfit from './sections/BestOutfit';
import TodaysPoll from './sections/TodaysPoll';
import BrandDiscounts from './sections/BrandDiscounts';

// 팔레트 회전 애니메이션
const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const DynamicSection = styled(Box)(({ theme }) => ({
  height: '100vh',
  position: 'relative',
  display: 'flex',
  overflow: 'hidden',
}));

const LeftPanel = styled(Box)(({ theme, bgcolor }) => ({
  width: '35%',
  height: '100%',
  position: 'relative',
  backgroundColor: bgcolor,
  transition: 'background-color 0.8s ease-in-out',
  display: 'flex',
  flexDirection: 'column',
  padding: '50px',
  color: '#fff',
}));

const RightPanel = styled(Box)(({ theme }) => ({
  width: '65%',
  height: '100%',
  backgroundColor: '#fff',
  position: 'relative',
  padding: '50px 60px',
  transition: 'opacity 0.5s ease-in-out',
  overflow: 'hidden',
}));

const PaletteContainer = styled(Box)(({ theme }) => ({
  width: '80px',
  height: '80px',
  position: 'relative',
  marginBottom: '40px',
}));

const PaletteSegment = styled(Box)(({ color, isActive }) => ({
  width: '80px',
  height: '80px',
  borderRadius: '50%',
  position: 'absolute',
  backgroundColor: color,
  opacity: isActive ? 1 : 0.7,
  transition: 'opacity 0.3s ease-in-out',
  cursor: 'pointer',
  '&:hover': {
    opacity: 1,
  },
}));

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const ContentContainer = styled(Box)(({ theme }) => ({
  animation: `${fadeIn} 0.5s ease-out forwards`,
}));

const ScrollSection = styled(Box)(({ theme }) => ({
  backgroundColor: '#f5f7fa',
  padding: '60px 0',
  minHeight: '100vh',
}));

const sections = [
  {
    id: 'values',
    color: '#87CEEB',
    title: '우리의 가치관',
    component: Values,
    preview: () => (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography variant="h4" gutterBottom>
          색각 이상자를 위한 패션의 새로운 기준
        </Typography>
        <Typography variant="subtitle1">
          모든 사람이 자신만의 스타일을 자유롭게 표현할 수 있는 공간
        </Typography>
      </Box>
    ),
  },
  {
    id: 'hot',
    color: '#FF6347',
    title: 'Hot 게시물',
    component: HotPosts,
    preview: () => <HotPosts preview={true} />,
  },
  {
    id: 'best-outfit',
    color: '#FFDA63',
    title: '오늘의 Best 코디',
    component: BestOutfit,
    preview: () => <BestOutfit preview={true} />,
  },
  {
    id: 'poll',
    color: '#B0B0B0',
    title: '오늘의 투표',
    component: TodaysPoll,
    preview: () => <TodaysPoll preview={true} />,
  },
  {
    id: 'event',
    color: '#F5F5F5',
    title: '할인 정보 & Event',
    component: BrandDiscounts,
    preview: () => <BrandDiscounts preview={true} />,
  },
];

const MainLayout = () => {
  const [currentSection, setCurrentSection] = useState(0);
  const [isAutoRotating, setIsAutoRotating] = useState(true);
  const [contentKey, setContentKey] = useState(0);

  useEffect(() => {
    let interval;
    if (isAutoRotating) {
      interval = setInterval(() => {
        setCurrentSection((prev) => (prev + 1) % sections.length);
        setContentKey((prev) => prev + 1);
      }, 3000);
    }
    return () => clearInterval(interval);
  }, [isAutoRotating]);

  const handlePaletteClick = (index) => {
    setCurrentSection(index);
    setContentKey((prev) => prev + 1);
    setIsAutoRotating(false);
    setTimeout(() => setIsAutoRotating(true), 10000);
  };

  const PreviewComponent = sections[currentSection].preview;

  return (
    <Box>
      {/* 상단 동적 영역 */}
      <DynamicSection>
        <LeftPanel bgcolor={sections[currentSection].color}>
          <PaletteContainer>
            {sections.map((section, index) => (
              <PaletteSegment
                key={section.id}
                color={section.color}
                isActive={currentSection === index}
                onClick={() => handlePaletteClick(index)}
                sx={{
                  transform: `rotate(${(360 / sections.length) * index}deg)`,
                }}
              />
            ))}
          </PaletteContainer>
          
          <Typography
            variant="h2"
            sx={{
              fontSize: '3.2rem',
              fontWeight: 700,
              marginBottom: '20px',
              textShadow: '0 2px 4px rgba(0,0,0,0.2)',
            }}
          >
            {sections[currentSection].title}
          </Typography>
        </LeftPanel>

        <RightPanel>
          <ContentContainer key={contentKey}>
            <PreviewComponent />
          </ContentContainer>
        </RightPanel>
      </DynamicSection>

      {/* 하단 스크롤 영역 */}
      <ScrollSection>
        <Container maxWidth="lg">
          <Grid container spacing={4}>
            {/* Hot 게시물 섹션 */}
            <Grid item xs={12}>
              <Typography variant="h4" gutterBottom sx={{ 
                borderBottom: '3px solid #FF6347',
                paddingBottom: 2,
                marginBottom: 4
              }}>
                인기 게시물
              </Typography>
              <HotPosts />
            </Grid>

            {/* 오늘의 코디 섹션 */}
            <Grid item xs={12}>
              <Typography variant="h4" gutterBottom sx={{ 
                borderBottom: '3px solid #FFDA63',
                paddingBottom: 2,
                marginBottom: 4
              }}>
                오늘의 코디
              </Typography>
              <BestOutfit />
            </Grid>

            {/* 이벤트 & 투표 섹션 */}
            <Grid item xs={12} md={8}>
              <Typography variant="h4" gutterBottom sx={{ 
                borderBottom: '3px solid #87CEEB',
                paddingBottom: 2,
                marginBottom: 4
              }}>
                진행중인 이벤트
              </Typography>
              <BrandDiscounts />
            </Grid>

            <Grid item xs={12} md={4}>
              <Typography variant="h4" gutterBottom sx={{ 
                borderBottom: '3px solid #B0B0B0',
                paddingBottom: 2,
                marginBottom: 4
              }}>
                실시간 투표
              </Typography>
              <TodaysPoll />
            </Grid>
          </Grid>
        </Container>
      </ScrollSection>
    </Box>
  );
};

export default MainLayout;
