import React from 'react';
import { Box, Grid, Card, CardContent, Typography, Avatar, Chip, IconButton } from '@mui/material';
import { styled } from '@mui/material/styles';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';

const PostCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.2s ease-in-out',
  '&:hover': {
    transform: 'translateY(-4px)',
  },
}));

const CardTag = styled(Chip)(({ tagColor }) => ({
  backgroundColor: tagColor,
  color: '#fff',
  marginBottom: 2,
}));

const posts = [
  {
    id: 1,
    tag: '코디 추천',
    tagColor: '#87CEEB',
    title: '색각 이상자를 위한 가을 코디 추천',
    content: '가을에 어울리는 색상 조합과 패턴 매칭 방법을 소개합니다.',
    author: '패션러버',
    likes: 128,
    comments: 45,
  },
  {
    id: 2,
    tag: '정보 공유',
    tagColor: '#FF6347',
    title: '색상 구분이 쉬운 브랜드 모음',
    content: '색각 이상자 친화적인 의류 브랜드들을 정리해보았습니다.',
    author: '스타일가이드',
    likes: 96,
    comments: 32,
  },
  {
    id: 3,
    tag: '질문',
    tagColor: '#FFDA63',
    title: '이 두 색상 조합 어떠신가요?',
    content: '회사 면접용 정장 색상 조합 문의드립니다.',
    author: '면접준비생',
    likes: 64,
    comments: 28,
  },
];

const HotPosts = ({ preview = false }) => {
  const displayPosts = preview ? posts.slice(0, 2) : posts;

  return (
    <Grid container spacing={3}>
      {displayPosts.map((post) => (
        <Grid item xs={12} md={preview ? 6 : 4} key={post.id}>
          <PostCard>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <CardTag label={post.tag} tagColor={post.tagColor} />
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ width: 24, height: 24, mr: 1 }}>{post.author[0]}</Avatar>
                  <Typography variant="body2" color="text.secondary">
                    {post.author}
                  </Typography>
                </Box>
              </Box>
              
              <Typography variant="h6" gutterBottom>
                {post.title}
              </Typography>
              
              <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                {post.content}
              </Typography>
              
              <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 'auto' }}>
                <IconButton size="small" color="primary">
                  <FavoriteIcon fontSize="small" />
                  <Typography variant="body2" sx={{ ml: 0.5 }}>
                    {post.likes}
                  </Typography>
                </IconButton>
                <IconButton size="small" color="primary">
                  <ChatBubbleOutlineIcon fontSize="small" />
                  <Typography variant="body2" sx={{ ml: 0.5 }}>
                    {post.comments}
                  </Typography>
                </IconButton>
              </Box>
            </CardContent>
          </PostCard>
        </Grid>
      ))}
    </Grid>
  );
};

export default HotPosts;
