import React from 'react';
import { Box, Card, CardContent, CardMedia, Typography, Avatar, IconButton, Chip } from '@mui/material';
import { styled } from '@mui/material/styles';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ColorLensIcon from '@mui/icons-material/ColorLens';

const OutfitCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.2s ease-in-out',
  '&:hover': {
    transform: 'translateY(-4px)',
  },
}));

const ColorChip = styled(Chip)(({ bgcolor }) => ({
  backgroundColor: bgcolor,
  color: '#fff',
  margin: '0 4px',
  minWidth: '60px',
}));

const bestOutfit = {
  id: 1,
  title: '가을 데일리룩',
  image: 'https://source.unsplash.com/random/400x500/?autumn,fashion',
  author: '스타일리스트김',
  likes: 256,
  colors: [
    { name: '베이지', code: '#D4C4B7' },
    { name: '브라운', code: '#8B4513' },
    { name: '네이비', code: '#000080' },
  ],
  description: '가을에 어울리는 차분한 톤의 데일리 코디입니다. 베이지 코트와 네이비 팬츠의 조화가 포인트!',
};

const BestOutfit = ({ preview = false }) => {
  return (
    <OutfitCard>
      <CardMedia
        component="img"
        height={preview ? "300" : "400"}
        image={bestOutfit.image}
        alt={bestOutfit.title}
      />
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Avatar sx={{ width: 32, height: 32, mr: 1 }}>{bestOutfit.author[0]}</Avatar>
            <Typography variant="subtitle1">
              {bestOutfit.author}
            </Typography>
          </Box>
          <Typography variant="body2" color="text.secondary">
            {bestOutfit.likes} likes
          </Typography>
        </Box>

        <Typography variant="h6" gutterBottom>
          {bestOutfit.title}
        </Typography>

        {!preview && (
          <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
            {bestOutfit.description}
          </Typography>
        )}

        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <ColorLensIcon sx={{ mr: 1, color: 'text.secondary' }} />
          {bestOutfit.colors.map((color, index) => (
            <ColorChip
              key={index}
              label={color.name}
              bgcolor={color.code}
              size="small"
            />
          ))}
        </Box>

        <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 'auto' }}>
          <IconButton size="small">
            <FavoriteIcon />
          </IconButton>
          <IconButton size="small">
            <ShareIcon />
          </IconButton>
        </Box>
      </CardContent>
    </OutfitCard>
  );
};

export default BestOutfit;
