import React, { useState } from 'react';
import { Box, Typography, Paper, RadioGroup, FormControlLabel, Radio, Button, LinearProgress } from '@mui/material';
import { styled } from '@mui/material/styles';
import HowToVoteIcon from '@mui/icons-material/HowToVote';

const PollCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
}));

const OptionBox = styled(Box)(({ theme, selected, hasVoted }) => ({
  padding: theme.spacing(2),
  marginBottom: theme.spacing(2),
  border: `1px solid ${selected ? theme.palette.primary.main : theme.palette.divider}`,
  borderRadius: theme.shape.borderRadius,
  cursor: hasVoted ? 'default' : 'pointer',
  position: 'relative',
  transition: 'all 0.3s ease',
  '&:hover': {
    backgroundColor: hasVoted ? 'transparent' : theme.palette.action.hover,
  },
}));

const ProgressBar = styled(LinearProgress)(({ theme }) => ({
  height: 8,
  borderRadius: 4,
  backgroundColor: theme.palette.grey[200],
  '& .MuiLinearProgress-bar': {
    borderRadius: 4,
  },
}));

const TodaysPoll = () => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [hasVoted, setHasVoted] = useState(false);

  const pollData = {
    question: '색각이상자를 위한 가장 유용한 패션 도구는?',
    options: [
      {
        id: 1,
        text: '색상 인식 앱',
        votes: 45,
        description: '스마트폰으로 색상을 실시간 인식하는 앱',
      },
      {
        id: 2,
        text: '컬러 팔레트 카드',
        votes: 32,
        description: '휴대용 컬러 매칭 가이드 카드',
      },
      {
        id: 3,
        text: '의류 태그 리더',
        votes: 28,
        description: '의류 태그의 색상 정보를 읽어주는 기기',
      },
    ],
  };

  const totalVotes = pollData.options.reduce((sum, option) => sum + option.votes, 0);

  const handleVote = () => {
    if (selectedOption && !hasVoted) {
      setHasVoted(true);
    }
  };

  return (
    <Box sx={{ py: 4 }}>
      <Typography variant="h4" component="h2" gutterBottom align="center" sx={{ mb: 6 }}>
        오늘의 투표
      </Typography>
      <PollCard elevation={3}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <HowToVoteIcon sx={{ fontSize: 32, mr: 2, color: 'primary.main' }} />
          <Typography variant="h5" component="h3">
            {pollData.question}
          </Typography>
        </Box>

        <RadioGroup
          value={selectedOption}
          onChange={(e) => !hasVoted && setSelectedOption(Number(e.target.value))}
        >
          {pollData.options.map((option) => {
            const percentage = Math.round((option.votes / totalVotes) * 100);
            
            return (
              <OptionBox
                key={option.id}
                selected={selectedOption === option.id}
                hasVoted={hasVoted}
                onClick={() => !hasVoted && setSelectedOption(option.id)}
              >
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                  <FormControlLabel
                    value={option.id}
                    control={<Radio />}
                    label={option.text}
                    sx={{ flexGrow: 1 }}
                  />
                  {hasVoted && (
                    <Typography variant="body2" color="text.secondary">
                      {percentage}%
                    </Typography>
                  )}
                </Box>
                
                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                  {option.description}
                </Typography>

                {hasVoted && (
                  <Box sx={{ width: '100%' }}>
                    <ProgressBar variant="determinate" value={percentage} />
                  </Box>
                )}
              </OptionBox>
            );
          })}
        </RadioGroup>

        <Button
          variant="contained"
          fullWidth
          disabled={!selectedOption || hasVoted}
          onClick={handleVote}
          sx={{ mt: 2 }}
        >
          {hasVoted ? '투표 완료' : '투표하기'}
        </Button>

        {hasVoted && (
          <Typography
            variant="body2"
            color="text.secondary"
            align="center"
            sx={{ mt: 2 }}
          >
            총 {totalVotes}명 참여
          </Typography>
        )}
      </PollCard>
    </Box>
  );
};

export default TodaysPoll;
